import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Paper,
  Typography,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  CircularProgress,
  Alert
} from '@mui/material';
import BookAppointment from './BookAppointment';
import DoctorDetails from './DoctorDetails';
import './DoctorList.css';

const DoctorList = () => {
  const [specialties, setSpecialties] = useState([]);
  const [doctors, setDoctors] = useState([]);
  const [selectedSpecialty, setSelectedSpecialty] = useState('');
  const [openAppointment, setOpenAppointment] = useState(false);
  const [openDetails, setOpenDetails] = useState(false);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch specialties from the backend
  useEffect(() => {
    fetch('http://localhost:8080/doctors/speciality')
      .then(response => response.json())
      .then(data => setSpecialties(data))
      .catch(error => {
        console.error('Error fetching specialties:', error);
        setError('Failed to load specialties');
      });
  }, []);

  // Fetch doctors from the backend with filtering
  useEffect(() => {
    setLoading(true);
    axios.get('http://localhost:8080/doctors', {
      params: { specialty: selectedSpecialty }
    })
      .then(response => {
        console.log('Fetched doctors:', response.data);
        setDoctors(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching doctors:', error);
        setError('Failed to load doctors');
        setLoading(false);
      });
  }, [selectedSpecialty]);

  const handleBookAppointment = (doctor) => {
    setSelectedDoctor(doctor);
    setOpenAppointment(true);
  };

  const handleViewDetails = (doctor) => {
    setSelectedDoctor(doctor);
    setOpenDetails(true);
  };

  if (loading) return <CircularProgress />;
  if (error) return <Alert severity="error">{error}</Alert>;

  return (
    <div>
      <FormControl fullWidth margin="normal">
        <InputLabel>Specialty</InputLabel>
        <Select
          value={selectedSpecialty}
          onChange={(e) => setSelectedSpecialty(e.target.value)}
          label="Specialty"
        >
          <MenuItem value="">All</MenuItem>
          {specialties.map((specialty) => (
            <MenuItem key={specialty} value={specialty}>{specialty}</MenuItem>
          ))}
        </Select>
      </FormControl>
      {doctors.length === 0 ? (
        <Typography variant="body1">No doctors available for the selected specialty.</Typography>
      ) : (
        doctors.map((doctor) => (
          <Paper key={doctor.id} style={{ margin: 15, padding: 20, cursor: 'pointer' }}>
            <Typography variant="h6">{doctor.name || "No Name"}</Typography>
            <Typography variant="body1">Specialty: {doctor.specialty || "No Specialty"}</Typography>
            <Typography variant="body1">Rating: {doctor.rating || "No Rating"}</Typography>
            <Button
              variant="contained"
              color="primary"
              style={{ marginRight: 10 }}
              onClick={() => handleBookAppointment(doctor)}
            >
              BOOK APPOINTMENT
            </Button>
            <Button
              variant="contained"
              color="success"
              onClick={() => handleViewDetails(doctor)}
            >
              VIEW DETAILS
            </Button>
          </Paper>
        ))
      )}
      {selectedDoctor && (
        <>
          <BookAppointment
            open={openAppointment}
            onClose={() => setOpenAppointment(false)}
            doctor={selectedDoctor}
          />
          <DoctorDetails
            open={openDetails}
            onClose={() => setOpenDetails(false)}
            doctor={selectedDoctor}
          />
        </>
      )}
    </div>
  );
};

export default DoctorList;
