// Header.js
import React, { useState } from 'react';
import Button from '@mui/material/Button';
import Modal from 'react-modal';
import { Tabs, Tab, Card, CardContent } from '@mui/material';
import Login from '../../screens/login/Login';
import Register from '../../screens/register/Register'; // Import the Register component
import './Header.css';
import logo from '../../assets/logo.jpeg';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import axios from 'axios';

const Header = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [tabValue, setTabValue] = useState(0);
    const { isLoggedIn, setIsLoggedIn } = useAuth();
    const navigate = useNavigate();

    const openModal = () => setIsModalOpen(true);
    const closeModal = () => setIsModalOpen(false);

    const handleTabChange = (event, newValue) => setTabValue(newValue);

    const handleLogout = async () => {
        const token = localStorage.getItem('BEARER_TOKEN');
        if (!token) return;

        try {
            await axios.post('/auth/logout', {}, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            localStorage.clear();
            setIsLoggedIn(false);
        } catch (error) {
            console.error('Logout error:', error);
        }
    };

    const handleLoginClick = () => openModal();

    return (
        <div className="header">
            <div className="header-left">
                <div className="logo">
                    <img src={logo} alt="Logo" style={{ width: '35px', height: '35px' }} />
                </div>
                <span className="app-name">Doctor Finder</span>
            </div>
            <div className="header-right">
                {!isLoggedIn ? (
                    <Button variant="contained" color="primary" onClick={handleLoginClick}>
                        Login
                    </Button>
                ) : (
                    <Button variant="contained" color="secondary" onClick={handleLogout}>
                        Logout
                    </Button>
                )}
            </div>

            <Modal
                isOpen={isModalOpen}
                onRequestClose={closeModal}
                className="modal"
                overlayClassName="overlay"
            >
                <Card>
                    <Tabs value={tabValue} onChange={handleTabChange} centered>
                        <Tab label="LOGIN" />
                        <Tab label="REGISTER" />
                    </Tabs>
                    <CardContent>
                        {tabValue === 0 && <Login onLoginSuccess={() => setIsLoggedIn(true)} />}
                        {tabValue === 1 && <Register />}
                    </CardContent>
                </Card>
            </Modal>
        </div>
    );
};

export default Header;
