import React, { useState } from 'react';
import { Button, TextField, Typography, CircularProgress, Alert } from '@mui/material';
import axios from 'axios';
import './Login.css';

const Login = ({ onLoginSuccess }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    const handleLogin = async () => {
        setIsLoading(true);
        setError('');

        try {
            const response = await axios.post('/auth/login', {
                username: username,
                password: password
            });

            if (response.status === 200) {
                const token = response.data.accessToken; // Retrieve the token from response body
                localStorage.setItem('BEARER_TOKEN', token); // Store the token in local storage
                onLoginSuccess(); // Notify parent component of successful login
            }
        } catch (err) {
            setError('Invalid username or password');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="login-container">
            <Typography variant="h5" component="h2" className="login-title">
                Login
            </Typography>
            {error && <Alert severity="error">{error}</Alert>}
            <TextField
                label="Username"
                variant="outlined"
                fullWidth
                margin="normal"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
            />
            <TextField
                label="Password"
                variant="outlined"
                fullWidth
                margin="normal"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            />
            <Button
                variant="contained"
                color="primary"
                fullWidth
                className="login-button"
                onClick={handleLogin}
                disabled={isLoading}
            >
                {isLoading ? <CircularProgress size={24} /> : 'Login'}
            </Button>
        </div>
    );
};

export default Login;
