import React, { useState } from 'react';
import { TextField, Button, Typography, MenuItem, Select, FormControl, InputLabel } from '@mui/material';
// import { DatePicker } from '@mui/x-date-pickers';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';

import { useNavigate, useLocation } from 'react-router-dom';
// import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
// import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider } from '@mui/x-date-pickers';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';


const BookAppointment = () => {
  const [date, setDate] = useState(new Date());
  const [timeSlot, setTimeSlot] = useState('');
  const [medicalHistory, setMedicalHistory] = useState('');
  const [symptoms, setSymptoms] = useState('');
  const [timeSlotError, setTimeSlotError] = useState('');

  const location = useLocation();
  const navigate = useNavigate();

  const { doctor } = location.state || {};

  if (!doctor || !doctor.firstName || !doctor.lastName) {
    return <Typography variant="h6" color="error">No doctor details available. Please go back and select a doctor.</Typography>;
  }

  const handleDateChange = (date) => setDate(date);

  const handleBookAppointment = () => {
    if (!timeSlot) {
      setTimeSlotError('Select a time slot');
      return;
    }
    // Booking logic here (e.g., send to backend)
    navigate('/appointments'); // Redirect after booking
  };

  return (
    <div style={{ padding: '20px' }}>
      <Typography variant="h4">Book an Appointment</Typography>
      <TextField
        fullWidth
        label="Doctor's Name"
        value={`${doctor.firstName} ${doctor.lastName}`}
        InputProps={{ readOnly: true }}
        margin="normal"
      />
      <LocalizationProvider dateAdapter={AdapterDateFns}>
        <DatePicker
          margin="normal"
          label="Date"
          format="MM/dd/yyyy"
          value={date}
          onChange={handleDateChange}
          fullWidth
        />
      </LocalizationProvider>
      <FormControl fullWidth margin="normal">
        <InputLabel>Time Slot</InputLabel>
        <Select
          value={timeSlot}
          onChange={(e) => {
            setTimeSlot(e.target.value);
            setTimeSlotError('');
          }}
        >
          <MenuItem value="09:00">09:00 AM</MenuItem>
          <MenuItem value="10:00">10:00 AM</MenuItem>
          {/* Add more time slots here */}
        </Select>
        {timeSlotError && <Typography color="error">{timeSlotError}</Typography>}
      </FormControl>
      <TextField
        fullWidth
        label="Medical History"
        value={medicalHistory}
        onChange={(e) => setMedicalHistory(e.target.value)}
        margin="normal"
      />
      <TextField
        fullWidth
        label="Symptoms"
        value={symptoms}
        onChange={(e) => setSymptoms(e.target.value)}
        margin="normal"
      />
      <Button
        variant="contained"
        color="primary"
        onClick={handleBookAppointment}
        style={{ marginTop: 20 }}
      >
        BOOK APPOINTMENT
      </Button>
    </div>
  );
};

export default BookAppointment;
